import './App.css';
import { Container, Dropdown, Form, Label,Pagination, Segment, Grid,  Header, Button, Input, Table} from 'semantic-ui-react';
import React, { useEffect, useState } from 'react';
import { ReactDatez } from 'react-datez';
import moment from "moment";
import Restaurants from "../src/Data/Restaurant.json";

function App() {
    const [restaurantIds, setRestaurantIds] = useState([]);
    const [fromDate, setFromDate] = useState("2021-10-01");
    const [toDate, settoDate] = useState("2021-10-15");
    const [fromHour,setfromHour ] = useState(6);
    const [toHour,settoHour ] = useState(29);
    const [transactionDataState, setTransactionsDataState] = useState([]);
    const [activePage, setActivePage] = useState(1);
    const [metricCode,setmetricCode ] = useState("");
    const [metricDefinitions, setMetricDefinitions] = useState([]);
    const [CompareType,setCompareType ] = useState("");
    const [value,setValue ] = useState(0);


    const submit = () => {
        const input = {
            restaurantIds: restaurantIds,
            fromDate: fromDate,
            toDate: toDate,
            fromHour:fromHour,
            toHour: toHour,
            metricCriteria: [
              {
                "metricCode": metricCode,
                "compareType": CompareType,
                "value": +value
              }
            ]
          }
    console.log(input)
    postData("https://customsearchquerytoolapi.azurewebsites.net/Search/Query", input)
        .then(data => {
        setTransactionsDataState(data);
    });
        console.log(transactionDataState);

    }

    const optionshours = [
      { key: 1, text: '6am', value: 6 },
      { key: 2, text: '7am', value: 7 },
      { key: 3, text: '8am', value: 8 },
      { key: 4, text: '9am', value: 9 },
      { key: 5, text: '10am', value: 10 },
      { key: 6, text: '11am', value: 11 },
      { key: 7, text: '12pm', value: 12 },
      { key: 8, text: '1pm', value: 13 },
      { key: 9, text: '2pm', value: 14},
      { key: 10, text: '3pm', value: 15 },
      { key: 11, text: '4pm', value: 16 },
      { key: 12, text: '5pm', value: 17 },
      //all the way to 5am 
      //6am = value 6 go over 24 
      //do the same for the key

    ]
    const compareOperators = [
        { key: 1, text: '≤', value: 'LessThanOrEqual' },
        { key: 2, text: '<', value: 'LessThan' },
        { key: 3, text: '=', value: 'Equal' },
        { key: 4, text: '>', value: "GreaterThan" },
        { key: 5, text: '≥', value: "GreaterThanOrEqual" },


      ]





    useEffect(() => {
        getData("https://customsearchquerytoolapi.azurewebsites.net/Search/MetricDefinitions")
            .then(data => {
                setMetricDefinitions(data);
            });
    }, []);

    //similar with the transaction 
    //to do
    //1. create a function to do the post request (similar to getData)



    console.log(metricDefinitions);

    const handlePaginationChange = (pageNumber) => {
        setActivePage(pageNumber);
        console.log(pageNumber);
    }

    const options = Restaurants.map(r => {
        return {
            key: r.Id,
            value: r.Id,
            text: r.Name
        }
    });

    const metricCodeOptions = metricDefinitions.map(m => {
        return {
            key: m.metricCode,
            value: m.metricCode,
            text: m.alias
        }
    });


      console.log(restaurantIds);
      console.log(metricCode);
      const transactionDataPaginated =  [transactionDataState[0]];

      console.log(transactionDataPaginated);

  return (

    <Container>
      <Grid container style={{ padding: '2em 0em' }}>
        <Grid.Row>
          <Grid.Column>
               <Header as='h1' dividing>
                  Restaurant Search Query
               </Header>
          </Grid.Column>
       </Grid.Row>
    </Grid>


        <Segment>
            <Form onSubmit={() => submit()}>
                <Form.Field>
                    <Dropdown placeholder='Restaurant Ids'
                        fluid
                        multiple
                        selection
                        options={options}
                        value={restaurantIds}
                        onChange={(event, data) => {
                            setRestaurantIds(data.value)
                        }} />
                </Form.Field>
                <Form.Group>
                    <Form.Field>
                        <label>Start Date</label>
                        <ReactDatez
                            name="dateInput"
                            handleChange={(value) => setFromDate(moment(value).format("YYYY-MM-DD"))}
                            value={fromDate}
                            allowPast={true}
                            dateFormat={"MM/DD/YYYY"}
                            placeholder={"MM/DD/YYYY"}
                            startDate={"2021-10-01"}
                            endDate={"2021-10-26"}
                        />
                    </Form.Field>
                    <Form.Field>
                        <label>End Date</label>
                        <ReactDatez
                            name="dateInput"
                            handleChange={(value) => settoDate(moment(value).format("YYYY-MM-DD"))}
                            value={toDate}
                            allowPast={true}
                            dateFormat={"MM/DD/YYYY"}
                            placeholder={"MM/DD/YYYY"}
                            startDate={"2021-10-01"}
                            endDate={"2021-10-26"}
                        />
                    </Form.Field>

                    <Form.Field>
                        <label> From Hours</label>
                        <Dropdown  

                                label='Hours'
                                placeholder='Hours'
                                fluid selection
                                options={optionshours}
                                value={fromHour}
                                onChange={(event, data) => {
                                setfromHour(data.value)
                        }}
                                />

                        </Form.Field>



                            <Form.Field>
                            <label> To Hours</label>
                            <Dropdown  

                                    label='Hours'
                                    placeholder='Hours'
                                    fluid selection
                                    options={optionshours}
                                    value={toHour}
                                    onChange={(event, data) => {
                                    settoHour(data.value)
                                    }}/>

                        </Form.Field>
                        </Form.Group>



                <Form.Group>
                    <Form.Field>
                     <label> Metric</label>
                        <Dropdown placeholder='Metric Code'
                        fluid
                        selection
